import React, { Component } from 'react';
import axios from 'axios';
import "react-datepicker/dist/react-datepicker.css";
var CanvasJSReact = require('../../ca');
var CanvasJS = CanvasJSReact.CanvasJS;
var CanvasJSChart = CanvasJSReact.CanvasJSChart;

export default class EditPost extends Component {
  constructor(props) {
    super(props);
    //ensures that the this called in the methods are the same this in the class
    this.onSubmit = this.onSubmit.bind(this);
    this.onChangeDiagnosis = this.onChangeDiagnosis.bind(this);
    this.createGraphOptions = this.createGraphOptions.bind(this)
    //state is how you create vars in react
    this.state = {
      username: '',
      description: '',
      date: new Date(),
      file: '',
      positive: false,
      graphData: [],
      data: ''
    }
  }
  createGraphOptions(jsonData){
    var optionsTemp = {
      animationEnabled: false,
      title: {
        text: "PCR Data"
      },
      axisY: {
        title: "sample y",
        includeZero: true
      },
      tooltip: {
        shared: true
      },
      data: []
    }
    var storage = []
    for (var i = 0; i < jsonData.length; i++){
      var whichLine = 0
      for (var dataKey of Object.keys(jsonData[i])){
        if (i === 0){
          storage.push({type: "spline", name: dataKey, showInLegends: true, dataPoints: []})
        }
        storage[whichLine].dataPoints.push({x: i, y: parseFloat(jsonData[i][dataKey])})
        whichLine++
      }
    }
    optionsTemp.data = storage
    return optionsTemp
  }
  createAxes(){
    return (
      [
        { primary: true, type: 'linear', position: 'bottom' },
        { type: 'linear', position: 'left' }
      ]
    )
  }
  componentDidMount() {
    console.log(this.props.match.params.id)
    axios.get('http://localhost:5000/posts/' + this.props.match.params.id)
      .then(response => {
        console.log(response.data)
        this.setState({
          username: response.data.username,
          description: response.data.description,
          date: new Date(response.data.date),
          file: response.data.file,
          data: response.data.data
        }, () => {
          if (this.state.data === "PCR"){
            console.log("is pcr yes")
            this.setState({ graphData: JSON.parse(this.state.file) })
          }
        })

      })
    axios.get('http://localhost:5000/users/')
      .then(response => {
        if (response.data.length > 0) {
          this.setState({
            users: response.data.map(user => user.username),
          })
        }
      })
      .catch((error) => {
        console.log(error);
      })

  }
  onChangeDiagnosis(e) {
    var res = false;
    if(e.target.value == 'Positive') {
      res = true;
    }
    this.setState({
      positive: res
    });
  }
  onSubmit(e) {
    e.preventDefault();

    const post = {
      username: this.state.username,
      description: this.state.description,
      date: this.state.date,
      file: this.state.file,
      complete: true,
      positive: this.state.positive
    }

    console.log(post);

    axios.post('http://localhost:5000/posts/update/' + this.props.match.params.id, post)
      .then(res => console.log(res.data));

    window.location = '/';
  }

  render() {
    return (
    <div>
      <h3>Label Image</h3>
      <form onSubmit={this.onSubmit}>
        <div className="form-group">
          <label>{this.state.username}'s Task</label>
        </div>
        <div className="form-group">
          <p>Instructions: {this.state.description}</p>
        </div>
        <div className="form-group">
          <label>Due Date: {String(this.state.date)}</label>
        </div>
        <div style={{display: 'flex',  justifyContent:'center', alignItems:'center'}}>
          {this.state.data === "PCR" ?
            <div>
              <CanvasJSChart options={this.createGraphOptions(this.state.graphData)}/>
            </div>
          :
            <img src={this.state.file} alt=""/>
          }
        </div>
        <div>
          <label>Result: </label>
          <select onChange={this.onChangeDiagnosis} defaultValue={'Negative'}>
            <option key='Positive' value='Positive'>Positive</option>
            <option key='Negative' value='Negative'>Negative</option>
          </select>
        </div>
        <div className="form-group">
          <input type="submit" value="Submit Task" className="btn btn-primary" />
        </div>
      </form>
    </div>
    )
  }
}
