import React, { Component } from 'react';
import axios from 'axios';
import "react-datepicker/dist/react-datepicker.css";
import LineChart from 'react-linechart'

export default class EditPost extends Component {
  constructor(props) {
    super(props);
    //ensures that the this called in the methods are the same this in the class
    this.onSubmit = this.onSubmit.bind(this);
    this.onChangeDiagnosis = this.onChangeDiagnosis.bind(this);
    this.onMouseDown = this.onMouseDown.bind(this);
    this.draw = this.draw.bind(this);
    this.onMouseMove = this.onMouseMove.bind(this);
    //state is how you create vars in react
    this.state = {
      username: '',
      description: '',
      date: new Date(),
      file: '',
      positive: false,
      loading: true,
      width: 0,
      height: 0,
      beginX: 0,
      beginY: 0,
      boxes: [],
      drawing: false,
      currentBox: {}
    }
  }
  componentDidMount() {
    axios.get('http://localhost:5000/posts/' + this.props.match.params.id)
      .then(response => {
        this.setState({
          username: response.data.username,
          description: response.data.description,
          date: new Date(response.data.date),
          file: response.data.file,
          data: response.data.data
        })
        if(response.data.data == 'Image') {
          this.setState({
            height: response.data.height,
            width: response.data.width
          });
        }
      })
  }
  initDraw(canvas) {
    function setMousePosition(e) {
        var ev = e || window.event; //Moz || IE
        if (ev.pageX) { //Moz
            mouse.x = ev.pageX + window.pageXOffset;
            mouse.y = ev.pageY + window.pageYOffset;
        } else if (ev.clientX) { //IE
            mouse.x = ev.clientX + document.body.scrollLeft;
            mouse.y = ev.clientY + document.body.scrollTop;
        }
    };

    var mouse = {
        x: 0,
        y: 0,
        startX: 0,
        startY: 0
    };
    var element = null;

    canvas.onmousemove = function (e) {
        setMousePosition(e);
        if (element !== null) {
            element.style.width = Math.abs(mouse.x - mouse.startX) + 'px';
            element.style.height = Math.abs(mouse.y - mouse.startY) + 'px';
            element.style.left = (mouse.x - mouse.startX < 0) ? mouse.x + 'px' : mouse.startX + 'px';
            element.style.top = (mouse.y - mouse.startY < 0) ? mouse.y + 'px' : mouse.startY + 'px';
        }
    }

    canvas.onclick = function (e) {
        if (element !== null) {
            element = null;
            canvas.style.cursor = "default";
            console.log("finsihed.");
        } else {
            console.log("begun.");
            mouse.startX = mouse.x;
            mouse.startY = mouse.y;
            element = document.createElement('div');
            element.className = 'rectangle'
            element.style.left = mouse.x + 'px';
            element.style.top = mouse.y + 'px';
            canvas.appendChild(element)
            canvas.style.cursor = "crosshair";
        }
    }
}
  onMouseDown(e) {
    var canvas = this.refs.canvas;
    let rect = canvas.getBoundingClientRect();
    let x = e.clientX - rect.left;
    let y = e.clientY - rect.top;
    this.state.currentBox.x = x;
    this.state.currentBox.y = y;
    var context = canvas.getContext('2d');
    this.setState({drawing: true});
    this.draw(canvas, context, this.state.currentBox, x+5, y+5);
  }
  onMouseMove(e) {
    if(this.state.drawing) {
      var canvas = this.refs.canvas;
      var context = canvas.getContext('2d')
      let rect = canvas.getBoundingClientRect();
      let x = e.clientX - rect.left;
      let y = e.clientY - rect.top;
      this.draw(canvas, context, this.state.currentBox, x, y);
    }
  }
  onChangeDiagnosis(e) {
    var res = false;
    if(e.target.value == 'Positive') {
      res = true;
    }
    this.setState({
      positive: res
    });
  }
  onSubmit(e) {
    e.preventDefault();

    const post = {
      username: this.state.username,
      description: this.state.description,
      date: this.state.date,
      file: this.state.file,
      complete: true,
      positive: this.state.positive
    }

    axios.post('http://localhost:5000/posts/update/' + this.props.match.params.id, post)
      .then(res => console.log(res.data));

    window.location = '/';
  }
  draw(canvas, context, box, tipX, tipY) {
    // the rectangle
    context.beginPath();
    context.rect(box.x, box.y, Math.abs(box.x-tipX), Math.abs(box.y-tipY));
    context.closePath();

    // the outline
    context.lineWidth = 3;
    context.strokeStyle = '#666666';
    context.stroke();
  }
  render() {
    if(this.state.data == 'Image') {
      var canvas = this.refs.canvas;
      var context = canvas.getContext('2d');
      var imageObj = this.refs.image;
      imageObj.onload = function() {
        context.drawImage(imageObj, 0, 0);
      };
    }
    return (
    <div>
      <h3>Label Image</h3>
      <form onSubmit={this.onSubmit}>
        <div className="form-group">
          <label>{this.state.username}'s Task</label>
        </div>
        <div className="form-group">
          <p>Instructions: {this.state.description}</p>
        </div>
        <div className="form-group">
          <label>Due Date: {String(this.state.date)}</label>
        </div>
        <div style={{display: 'flex',  justifyContent:'center', alignItems:'center'}}>
          <canvas ref="canvas" height={this.state.height}
          width={this.state.width} onMouseDown={this.onMouseDown} onMouseMove={this.onMouseMove}
          />
          <img ref="image" src={this.state.file} style={{display: 'none'}} />
        </div>
        <div>
          <label>Result: </label>
          <select onChange={this.onChangeDiagnosis} defaultValue={'Negative'}>
            <option key='Positive' value='Positive'>Positive</option>
            <option key='Negative' value='Negative'>Negative</option>
          </select>
        </div>
        <div className="form-group">
          <input type="submit" value="Submit Task" className="btn btn-primary" />
        </div>
      </form>
    </div>
    )
  }
}
